import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const ActivityGraph = ({ data }) => {
  return (
    <div className="activity-graph-container">
      {/* Header avec titre */}
      <div className="graph-header">
        <h3>Daily Activity</h3>
        {/* Légende Recharts apparaîtra naturellement dans le graphique */}
        {/* On laisse la div vide ou pour d'autres éléments si besoin */}
        <div className="legend-placeholder"></div>
      </div>

      <ResponsiveContainer width="100%" height={250}>
        <BarChart data={data} margin={{ top: 20, right: 20, bottom: 20, left: 0 }}>
          <XAxis dataKey="day" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="kilogram" fill="#282D30" />
          <Bar dataKey="calories" fill="#E60000" />
          <Legend verticalAlign="top" align="right" /> {/* légende standard */}
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ActivityGraph;
