import React from "react";
import {
  RadialBarChart,
  RadialBar,
  ResponsiveContainer
} from "recharts";
import "../styles/userScore.css";

function UserScore({ user }) {
  if (!user) return null;

  const score = (user.todayScore || user.score) * 100;

  const data = [
    {
      name: "score",
      value: score,
      fill: "#FF0000",
    },
    {
      name: "rest",
      value: 100 - score,
      fill: "transparent",
    },
  ];

  return (
    <div className="user-score">
      <h3>Score</h3>

      <ResponsiveContainer width="100%" height={250}>
        <RadialBarChart
          innerRadius="70%"
          outerRadius="80%"
          startAngle={90}
          endAngle={450}
          data={data}
        >
          <RadialBar
            dataKey="value"
            cornerRadius={10}
            stackId="score"
          />
        </RadialBarChart>
      </ResponsiveContainer>

      <div className="score-text">
        <strong>{score}%</strong>
        <span>de votre<br />objectif</span>
      </div>
    </div>
  );
}

export default UserScore;
