import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import CustomTooltipActivity from "./CustomTooltipActivity";
import "../styles/activityGraph.css";

function ActivityGraph({ data }) {
  if (!data) return null;

  // Légende personnalisée avec ronds
  const renderLegend = () => (
    <div
      style={{
        display: "flex",
        gap: "20px",
        justifyContent: "flex-end",
        marginBottom: "10px",
      }}
    >
      <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
        <div
          style={{
            width: "10px",
            height: "10px",
            borderRadius: "50%",
            backgroundColor: "#282D30",
          }}
        ></div>
        <span style={{ fontSize: "14px", color: "#74798C" }}>Poids (kg)</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
        <div
          style={{
            width: "10px",
            height: "10px",
            borderRadius: "50%",
            backgroundColor: "#E60000",
          }}
        ></div>
        <span style={{ fontSize: "14px", color: "#74798C" }}>
          Calories brûlées (kCal)
        </span>
      </div>
    </div>
  );

  return (
    <div className="activity-graph">
      <div>
        <h3>Activité quotidienne</h3>
      </div>

      <ResponsiveContainer width="100%" height="100%">
        <BarChart
          data={data}
          barGap={8}
          margin={{ top: 30, right: 30, left: 30, bottom: 10 }}
        >
          <CartesianGrid strokeDasharray="3 3" vertical={false} />

          <XAxis dataKey="day" tickLine={false} axisLine={false} />

          <YAxis
            yAxisId="kg"
            orientation="right"
            tickLine={false}
            axisLine={false}
            domain={["dataMin - 1", "dataMax + 1"]}
            allowDecimals={false}
          />

          <YAxis yAxisId="cal" hide />

          <Tooltip content={<CustomTooltipActivity />} />

          {/* Légende personnalisée */}
          <Legend content={renderLegend} verticalAlign="top" align="right" />

          <Bar
            yAxisId="kg"
            dataKey="kilogram"
            fill="#282D30"
            radius={[10, 10, 0, 0]}
            barSize={7}
            name="Poids (kg)"
          />

          <Bar
            yAxisId="cal"
            dataKey="calories"
            fill="#E60000"
            radius={[10, 10, 0, 0]}
            barSize={7}
            name="Calories brûlées (kCal)"
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default ActivityGraph;
