import React, { useState, useEffect } from "react";
import { getUser, getActivity, getAverageSessions, getPerformance } from "../services/api";
// import UserInfo from "../components/UserInfo";
import ActivityGraph from "../components/ActivityGraph";
import AverageSessionsGraph from "../components/AverageSessionsGraph";
import PerformanceGraph from "../components/PerformanceGraph";
import KpiCards from "../components/KpiCards";

const Dashboard = () => {
  const userId = 12; // ou 18
  const [userData, setUserData] = useState(null);
  const [activityData, setActivityData] = useState([]);
  const [sessionsData, setSessionsData] = useState([]);
  const [performanceData, setPerformanceData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const user = await getUser(userId);
      const activity = await getActivity(userId);
      const sessions = await getAverageSessions(userId);
      const performance = await getPerformance(userId);

      setUserData(user);
      setActivityData(activity.sessions);          // adapter selon ton back
      setSessionsData(sessions.sessions);         // idem
      setPerformanceData(performance.data);       // idem
    };
    fetchData();
  }, [userId]);

  if (!userData) return <div>Chargement...</div>;

  return (
    <div className="dashboard">
      <UserInfo data={userData.userInfos} score={userData.todayScore || userData.score} />
      <div className="graphs">
        <ActivityGraph data={activityData} />
        <AverageSessionsGraph data={sessionsData} />
        <PerformanceGraph data={performanceData} />
      </div>
      <KpiCards data={userData.keyData} />
    </div>
  );
};

export default Dashboard;
