import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "../styles/activity.css";

function ActivityGraph({ data }) {
  console.log(data);

  if (!data) return null;

  return (
    <div className="activity-graph">
      <h3>Activité quotidienne</h3>

      <ResponsiveContainer width="100%" height={250}>
        <BarChart
          data={data.day}
          barGap={8}
          margin={{ top: 30, right: 30, left: 30, bottom: 10 }}
        >
          <CartesianGrid strokeDasharray="3 3" vertical={false} />

          <XAxis
            dataKey="day"
            tickLine={false}
            axisLine={false}
          />

          <YAxis
            yAxisId="kg"
            orientation="right"
            tickLine={false}
            axisLine={false}
            domain={["dataMin - 1", "dataMax + 1"]}
          />

          <YAxis yAxisId="cal" hide />

          <Tooltip />

          <Legend verticalAlign="top" align="right" />

          <Bar
            yAxisId="kg"
            dataKey="kilogram"
            fill="#282D30"
            radius={[10, 10, 0, 0]}
            barSize={7}
            name="Poids (kg)"
          />

          <Bar
            yAxisId="cal"
            dataKey="calories"
            fill="#E60000"
            radius={[10, 10, 0, 0]}
            barSize={7}
            name="Calories brûlées (kCal)"
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default ActivityGraph;
