import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

import Header from "../components/Header";
import KpiCards from "../components/KpiCards";
import ActivityGraph from "../components/ActivityGraph";
import AverageSessionsGraph from "../components/AverageSessionsGraph";
import PerformanceGraph from "../components/PerformanceGraph";

import { getUser, getActivity, getAverageSessions, getPerformance } from "../services/api";

import "../styles/dashboard.css";

const Dashboard = () => {
  const { id } = useParams();

  const [userData, setUserData] = useState(null);
  const [activityData, setActivityData] = useState([]);
  const [sessionsData, setSessionsData] = useState([]);
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Appels API
        const user = await getUser(id);
        const activity = await getActivity(id);
        const sessions = await getAverageSessions(id);
        const performance = await getPerformance(id);

        // üîπ Extraire la propri√©t√© 'data' renvoy√©e par le backend
        setUserData(user.data);
        setActivityData(activity.data?.sessions || []);
        setSessionsData(sessions.data?.sessions || []);
        setPerformanceData(performance.data?.data || []);

        console.log("userData", user.data);
        console.log("activityData", activity.data?.sessions);
        console.log("sessionsData", sessions.data?.sessions);
        console.log("performanceData", performance.data?.data);

      } catch (error) {
        console.error("Erreur r√©cup√©ration donn√©es :", error);
      }
    };

    fetchData();
  }, [id]);

  // üîπ Affichage conditionnel si les donn√©es ne sont pas encore charg√©es
  if (!userData) return <div>Chargement...</div>;

  return (
    <div className="dashboard">
      <main className="main-dashboard">
        {/* Header dynamique */}
        <Header user={userData} />

        <div className="dashboard-content">
          <div className="graphs-section">
            {activityData.length > 0 && <ActivityGraph data={activityData} />}
            {sessionsData.length > 0 && <AverageSessionsGraph data={sessionsData} />}
            {performanceData.length > 0 && <PerformanceGraph data={performanceData} />}
          </div>

          <div className="kpi-section">
            {userData.keyData && <KpiCards data={userData.keyData} />}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
