import React from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  Rectangle,
} from "recharts";
import "../styles/sessionsAverage.css";

/**
 * Graph : Durée moyenne des sessions
 * Conforme maquette SportSee (OpenClassrooms)
 */
const AverageSessionsGraph = ({ data }) => {
  if (!data || data.length === 0) return null;

  const days = ["L", "M", "M", "J", "V", "S", "D"];

  // Formatage EXACT des données maquette
  const chartData = data.map((item) => ({
    day: days[item.day - 1],
    value: item.sessionLength,
  }));

  /**
   * Tooltip personnalisé (maquette)
   */
  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div className="sessions-tooltip">
          {payload[0].value} min
        </div>
      );
    }
    return null;
  };

  /**
   * Curseur sombre au survol (2e nuance de rouge)
   */
  const CustomCursor = ({ points, width, height }) => {
    const { x } = points[0];
    return (
      <Rectangle
        fill="rgba(0, 0, 0, 0.1)"
        x={x}
        y={0}
        width={width}
        height={height}
      />
    );
  };

  return (
    <div className="average-sessions-graph">
      {/* Titre DANS le graph */}
      <h3 className="sessions-title">Durée moyenne des sessions</h3>

      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          data={chartData}
          margin={{ top: 60, right: 20, left: 20, bottom: 20 }}
        >
          {/* Axe Y caché mais obligatoire */}
          <YAxis hide domain={["dataMin - 10", "dataMax + 10"]} />

          {/* Axe X jours */}
          <XAxis
            dataKey="day"
            axisLine={false}
            tickLine={false}
            stroke="rgba(255,255,255,0.6)"
            tick={{ fontSize: 12 }}
          />

          {/* Tooltip maquette */}
          <Tooltip
            content={<CustomTooltip />}
            cursor={<CustomCursor />}
          />

          {/* Courbe */}
          <Line
            type="monotone"
            dataKey="value"
            stroke="#FFFFFF"
            strokeWidth={2}
            dot={false}
            activeDot={{ r: 4, strokeWidth: 0 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default AverageSessionsGraph;
