import React from "react";
import { LineChart, Line, XAxis, Tooltip, ResponsiveContainer } from "recharts";

const AverageSessionsGraph = ({data}) => {
  // Si data est undefined, null ou vide (pour ne pas avoir bloquage)
  if (!data || data.length === 0) return <div>Chargement graphique...</div>;

  const daysMap = ["L", "M", "M", "J", "V", "S", "D"];
  // on crée un nouveau tableau
  const chartData = data.map((item) => ({    
    // day : lettre du jour / on commence à index 0
    day: daysMap[item.day - 1],
    // durée session
    sessionLength: item.sessionLength,
  }));

  return (
    <div className="average-sessions-graph">
      <h3>Moyenne des sessions</h3>
      <ResponsiveContainer width="20%" height={200}>
        <LineChart data={chartData}>
          <XAxis dataKey="day" />
          <Tooltip />
          <Line type="monotone" dataKey="sessionLength" stroke="#FFFFFF" strokeWidth={2} dot={false} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default AverageSessionsGraph;
