import React from "react";
// import des composants Recharts
import {BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer} from "recharts";

// props pour l'activité passé au parent (Dashboard)
function ActivityGraph({data}){
  return(
    <div className="activity-graph">
      <h3>Activité quotidienne</h3>
      <ResponsiveContainer width="100%" height={250}>
        {/* Conteneur graphique */}
        <BarChart
          data={data}
          margin={{top: 20, right: 20, left: 20, bottom: 5}}
        >
          <CartesianGrid strokeDasharray="3 3"/>
          <XAxis dataKey="calories" />
          <YAxis yAxisId="left" orientation="left" dataKey="kilogram"/>
          <YAxis yAxisId="right" orientation="right" dataKey="kilogram"/>
          <Tooltip/>
          <Legend/>
          <Bar
            yAxisId="left"
            dataKey="kilogram"
            fill="black"
            name="Poids (kg)"
            barSize={7}
          />
          <Bar
            yAxisId="right"
            dataKey="calories"
            fill="red"
            name="Calories brûlées (kCal)"
            barSize={7}
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
export default ActivityGraph;
