import { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
// import axios from 'axios';
import {getApi} from '../service/service';
import { BarChart, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import "../styles/activity.css"

function Activity() {
  // const [activity, setActivity] = useState(null)

  // useEffect(() => {
  //   async function testApi() {
  //     const activity = await getApi();
  //     setActivity(activity.data)
  //   };
  //   testApi();
  // }, []);

// ---------------------- DERNIERE VERSION -------------

let {id} = useParams();
console.log(id);

const [activity, setActivity] = useState(null)
useEffect(() => {
    getApi(id)
    .then(activity => {
      setActivity(activity);
    });
}, []);

console.log(activity);

  const data = [
    {name: "calories", value: `${activity.data.sessions[0].calories}`},
    {name: "kilogram", value: `${activity.data.sessions[0].kilogram}`}
  ]
  console.log(data);

  return (
    <>
      <h1>Activity graph</h1>
      <p>{activity?.data.userId}</p>
      <ResponsiveContainer width="100%" aspect={3}>
        <BarChart
          width={500}
          height={300}
          data={activity}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="calories" />
          <YAxis dataKey="kilogram" />
          <Tooltip />
          <Legend />
        </BarChart>
      </ResponsiveContainer>
    </>
  );
}
export default Activity;

