import React from "react";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  ResponsiveContainer,
} from "recharts";
import "../styles/performanceGraph.css";

/**
 * Graph : Performances
 * Conforme maquette SportSee (OpenClassrooms)
 */
const PerformanceGraph = ({ data }) => {
  if (!data || !data.data || !data.kind) return null;

  // Traduction FR des performances (maquette)
  const labelsFR = {
    cardio: "Cardio",
    energy: "Energie",
    endurance: "Endurance",
    strength: "Force",
    speed: "Vitesse",
    intensity: "Intensité",
  };

  // Formatage + inversion de l’ordre (EXACT maquette)
  const chartData = data.data
    .map((item) => ({
      subject: labelsFR[data.kind[item.kind]],
      value: item.value,
    }))
    .reverse();

  return (
    <div className="performance-graph">
      <ResponsiveContainer width="100%" height="100%">
        <RadarChart data={chartData} outerRadius="65%">
          <PolarGrid />
          <PolarAngleAxis
            dataKey="subject"
            stroke="#FFFFFF"
            tick={{ fontSize: 12 }}
          />
          <Radar
            dataKey="value"
            stroke="#FF0101"
            fill="#FF0101"
            fillOpacity={0.6}
          />
        </RadarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PerformanceGraph;
