import React from "react";
import {
  RadialBarChart,
  RadialBar,
  ResponsiveContainer
} from "recharts";
import "../styles/userScore.css";

function UserScore({ user }) {
  if (!user) return null;

  // Score utilisateur (12% ou 30%)
  const score = (user.todayScore || user.score) * 100;

  // Données du graphique
  const data = [
    {
      name: "score",
      value: score,
      fill: "#FF0000",
    },
  ];

  return (
    <div className="user-score">
      <h3>Score</h3>

      <ResponsiveContainer width="100%" height={250}>
        <RadialBarChart
          innerRadius="70%"
          outerRadius="80%"
          data={data}
          startAngle={90}
          endAngle={450}
          barSize={10}
          domain={[0, 100]} // C'est ici qu'on indique à recharts que la valeur max est 100, il va donc prendre la valeur renseignée et la comparer
        >
          <RadialBar
            minAngle={15}
            dataKey="value"
            cornerRadius={10}
          />
        </RadialBarChart>
      </ResponsiveContainer>

      <div className="score-text">
        <strong>{score}%</strong>
        <span>de votre<br />objectif</span>
      </div>
    </div>
  );
}

export default UserScore;
