import React from "react";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  ResponsiveContainer,
} from "recharts";

const PerformanceGraph = ({ data }) => {
  if (!data) return null;

  const kindLabels = {
    1: "Cardio",
    2: "Energie",
    3: "Endurance",
    4: "Force",
    5: "Vitesse",
    6: "Intensité",
  };

  const chartData = data.map((item) => ({
    subject: kindLabels[item.kind],
    value: item.value,
  }));

  return (
    <div className="performance-graph">
      <ResponsiveContainer width="100%" height={250}>
        <RadarChart data={chartData} outerRadius="70%">
          <PolarGrid />
          <PolarAngleAxis dataKey="subject" stroke="#FFFFFF" />
          <Radar
            dataKey="value"
            stroke="#FF0101"
            fill="#FF0101"
            fillOpacity={0.6}
          />
        </RadarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PerformanceGraph;
