import React from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

const ActivityGraph = ({ data }) => {
  // Transformation pour le graphique : jours et kg/cal
  const chartData = data.map((item, index) => ({
    day: index + 1,          // tu peux mettre les dates si tu veux
    kg: item.kilogram,
    cal: item.calories,
  }));

  return (
    <div className="activity-graph">
      <h3>Activit√© quotidienne</h3>
      <ResponsiveContainer width="100%" height={250}>
        <BarChart data={chartData} barGap={8}>
          <XAxis dataKey="day" />
          <YAxis yAxisId="kg" orientation="right" />
          <YAxis yAxisId="cal" orientation="left" hide />
          <Tooltip />
          <Bar yAxisId="kg" dataKey="kg" fill="#282D30" radius={[3, 3, 0, 0]} />
          <Bar yAxisId="cal" dataKey="cal" fill="#E60000" radius={[3, 3, 0, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ActivityGraph;
