function UserScore ({user}){
  if (!user || user.length === 0) return <div>Chargement graphique...</div>;
  const todayScore = user.todayScore;
  return(
    <PieChart width={400} height={400}>
          <Pie
            activeShape={{
              fill: 'red',
            }}
            data={[
              { name: 'Page A', uv: 590 },
              { name: 'Page B', uv: 590 },
              { name: 'Page C', uv: 868 },
            ]}
            dataKey="uv"
            isAnimationActive={todayScore}
          />
          <Tooltip defaultIndex={2} />
          <RechartsDevtools />
        </PieChart>
  )
}

export default UserScore;
