import React from "react";
import { PieChart, Pie, ResponsiveContainer } from "recharts";
import "../styles/userScore.css";

function UserScore({ user }) {
  if (!user) return null;

  const score = (user.todayScore || user.score) * 100;

  const data = [
    { name: "score", value: score },
    { name: "rest", value: 100 - score },
  ];

  return (
    <div className="user-score">
      <h3>Score</h3>

      <ResponsiveContainer width="100%" height={250}>
        <PieChart>
          {/* Cercle gris de fond */}
          <Pie
            data={[{ value: 100 }]}
            dataKey="value"
            innerRadius={70}
            outerRadius={80}
            fill="#FBFBFB"
            stroke="none"
          />

          {/* Arc rouge du score */}
          <Pie
            data={data}
            dataKey="value"
            startAngle={90}
            endAngle={450}
            innerRadius={70}
            outerRadius={80}
            fill="#FF0000"
            stroke="none"
            cornerRadius={10}
          />
        </PieChart>
      </ResponsiveContainer>

      {/* Texte centr√© */}
      <div className="score-text">
        <strong>{score}%</strong>
        <span>de votre objectif</span>
      </div>
    </div>
  );
}

export default UserScore;
