// api.js
const BASE_URL = "http://localhost:3000/user/";

export const getUser = async (id) => {
  try {
    const response = await fetch(`${BASE_URL}${id}`);
    return await response.json();
  } catch (err) {
    console.error("Erreur getUser:", err);
  }
};

export const getActivity = async (id) => {
  try {
    const response = await fetch(`${BASE_URL}${id}/activity`);
    return await response.json();
  } catch (err) {
    console.error("Erreur getActivity:", err);
  }
};

export const getAverageSessions = async (id) => {
  try {
    const response = await fetch(`${BASE_URL}${id}/average-sessions`);
    return await response.json();
  } catch (err) {
    console.error("Erreur getAverageSessions:", err);
  }
};

export const getPerformance = async (id) => {
  try {
    const response = await fetch(`${BASE_URL}${id}/performance`);
    return await response.json();
  } catch (err) {
    console.error("Erreur getPerformance:", err);
  }
};
